	public static void main(String[] args){
		long sum =0;
		int count = 0;
		Set<Long> set = new HashSet<>();
		for(long i=1;;i++){
			set.clear();
			sum +=i;
			set = check(sum);
			count = set.size();
			System.out.println(sum+"的公因子数量："+set.size());
			if(count >=500){
				System.out.println("结果："+sum);
				System.out.println("因子："+set);
				break;
			}
		}
	}
	
	public static HashSet<Long> check(long sum){
		Set<Long> set = new HashSet<>();
		for(long i=1;i<sum;i++){
			if(sum%i == 0){
				set.add(i);
				set.add(sum/i);
			}
		}
		return (HashSet<Long>) set;
	}

